def edwards_loop(min= nono[-1]+1,max=int('FFFF',16),debug=False):
    #min = nono[-1]+1 #int('03FF',16)
    #max = int('FFFF',16)
    for i in range(min,max):
        if i not in nono:
            for j in range(min,max):
                if j not in nono:
                    #print(format(i, '0>42b')) #binarno
                    #print(f'{i:064x}') #hex
                    P=int2hexstr(i)
                    Q=int2hexstr(j)
                    try:
                        ed=ed25519.edwards_add(binascii.unhexlify(P.encode()),binascii.unhexlify(Q.encode()))
                        if debug:
                            time_print('now: ',[str(i),str(j),str(int(binascii.hexlify(ed).decode(),16))])
                        else:
                            print('P',i,'Q',j,'ed',binascii.hexlify(ed).decode())
                    except Exception as ex:
                        #print('\t',i,j)
                        if j not in nono:
                            if i not in nono:
                                #print(j,i,ex)
                                print(f'{j},',end='')
                                nono.append(j)
                        pass
                    #print('nono =',nono)
    if debug: print(max,len(nono),nono)

def get_point():
    p = 2**255 - 19

    def inv(x):
        return pow(x, p-2, p)

    d = -121665 * inv(121666) % p
    I = pow(2,(p-1)//4,p)

    def findx(y):
        xx = (y*y-1) * inv(d*y*y+1)
        x = pow(xx,(p+3)//8,p)
        if (x*x - xx) % p != 0: x = (x*I) % p
        if x % 2 != 0: x = p-x
        return x

    y = 4 * inv(5)
    x = findx(y)

    return (x,y)

